// ReplyNotFound Reply With not found http code 404 code
func ReplyNotFound(w http.ResponseWriter, r *http.Request) {
	w.WriteHeader(http.StatusNotFound)
	w.Write([]byte("404 - Not found!"))
}

// ReplyErrorPage Reply With error page 500 code
func ReplyErrorPage(w http.ResponseWriter, r *http.Request) {
	w.WriteHeader(http.StatusInternalServerError)
	w.Write([]byte("500 - Something bad happened!"))
}

// ReplyNotImplemented Reply With not implemented 501 code
func ReplyNotImplemented(w http.ResponseWriter, r *http.Request) {
	w.WriteHeader(http.StatusNotImplemented)
	w.Write([]byte("501 - Not Implemented"))
}
func main() {
	// http.Handler
	mux := http.NewServeMux()
	mux.HandleFunc("/", ReplyBigHeader)
	mux.HandleFunc("/404", ReplyNotFound)
	mux.HandleFunc("/500", ReplyErrorPage)
	mux.HandleFunc("/501", ReplyNotImplemented)
	http.ListenAndServe(":8080", mux)
}
